css:
.main-div {
       padding: 10px;
       border: 1px solid #ddd;
       border-radius:5px;
       font-family: Arial, sans-serif;
       font-size: 14px;
   }

   .text-center {
       text-align: center;
   }

   .d-flex {
       display: flex;
   }

   .justify-content-between {
       justify-content: space-between;
   }

   .customer-details{
        width: 50%;
   }
   .invoice-details {
       width: 35%;
   }
   .customer-details p {

   }
   .customer-details h4,
   .invoice-details h4 {
       margin-bottom: 10px;
       color: #333;
   }

   .table {
       /*width: 80% !important;*/
       border-collapse: collapse;
       border: 1px solid #ddd !important;
       margin-top: 20px;
   }

   .table-bordered th,
   .table-bordered td {
       border: 1px solid #ddd !important;
       padding: 8px;
   }

   .table-light {
       background-color: #f9f9f9;
   }

   .text-end {
       text-align: right;
   }

   .mt-4 {
       margin-top: 20px;
   }

   .mb-4 {
       margin-bottom: 20px;
   }

   .mt-5 {
       margin-top: 30px;
   }

   p {
       margin: 0;
       line-height: 1.5;
   }

   h2 {
       color: #000;
       font-weight: bold;
   }

   h4 {
       color: #555;
   }
